import React from "react";

import "./Chart.css";

import ChartBar from "./ChartBar";

const Chart = (props) => {

  let monthValueList = props.expense.map(val => val.value)

  let maxVal = Math.max(...monthValueList);

  return (
    <div className="chart">
      {props.expense.map((dataPoint) => {
        return (
          <ChartBar
            key={dataPoint.label}
            value={dataPoint.value}
            maxValue={maxVal}
            label={dataPoint.label}
          />
        );
      })}
    </div>
  );
};

export default Chart;
